// 构建推理引擎的函数
bool SampleMNIST::build()
{
    // 构建推理引擎，需要传入ILogger 类的子类的对象
    auto builder = SampleUniquePtr(nvinfer1::createInferBuilder(gLogger.getTRTLogger()));
    if (!builder)
        return false;
    // 创建模型的网络对象
    auto network = SampleUniquePtr(builder->createNetwork());
    if (!network)
        return false;
    // 创建Caffe的解析器解析网络结构
    auto parser = SampleUniquePtr(nvcaffeparser1::createCaffeParser());
    if (!parser)
        return false;
    // 将构建器、网络对象、和解析器解析的网络结构进行关联，和网络结构填充。
    constructNetwork(builder, network, parser);
    // 设置网络结构定义的参数。
    builder->setMaxBatchSize(mParams.batchSize);
    builder->setMaxWorkspaceSize(16_MB);
    builder->allowGPUFallback(true);
    builder->setFp16Mode(mParams.fp16);
    builder->setInt8Mode(mParams.int8);
    builder->setStrictTypeConstraints(true);

    samplesCommon::enableDLA(builder.get(), mParams.dlaCore);
     // 执行推理和创建序列化文件
    mEngine = std::shared_ptr(builder->buildCudaEngine(*network), samplesCommon::InferDeleter());

    if (!mEngine)
        return false;

    assert(network->getNbInputs() == 1);
    mInputDims = network->getInput(0)->getDimensions();
    assert(mInputDims.nbDims == 3);

    return true;
}

3.2、部署阶段

代码解析（TensorRT中的sampleMNIST例程）

// 部署和应用TensorRT推理引擎
bool SampleMNIST::infer()
{
    // 创建一个缓冲区管理对象。
    samplesCommon::BufferManager buffers(mEngine, mParams.batchSize);

    auto context = SampleUniquePtr(mEngine->createExecutionContext());
    if (!context)
    {
        return false;
    }

    // 随机选择一个图片来进行推理
    srand(time(NULL));
    const int digit = rand() % 10;

    //  将输入数据读入管理的缓冲区
    //  这个输入的数据为一个batchSize大小的Tensor数据
    assert(mParams.inputTensorNames.size() == 1);
    if (!processInput(buffers, mParams.inputTensorNames[0], digit))
        return false;

    // 创建CUDA流对象执行推理
    cudaStream_t stream;
    CHECK(cudaStreamCreate(&stream));

    // 异步地将数据从主机输入缓冲区复制到（GPU）设备输入缓冲区
    buffers.copyInputToDeviceAsync(stream);

    // 异步的对排队的推理任务进行推理
    if (!context->enqueue(mParams.batchSize, buffers.getDeviceBindings().data(), stream, nullptr))
        return false;

    // 异步地将数据从设备输出缓冲区复制到主机输出缓冲区
    buffers.copyOutputToHostAsync(stream);

    // 等待CUDA工作流完成
    cudaStreamSynchronize(stream);

    // 释放CUDA工作流
    cudaStreamDestroy(stream);

    // 检查并打印推理的输出
    // 应该只有一个输出张量
    assert(mParams.outputTensorNames.size() == 1);
    bool outputCorrect = verifyOutput(buffers, mParams.outputTensorNames[0], digit);

    return outputCorrect;
}
